var fs = require('fs');
var filepath=require("path").resolve(__dirname,'../../../','assets/query_src');

/**
 * Basic query for the simple database
 * @basicquery  
 */

//var path = "static/asset/";



/* Return an object with 2 properties:
    query: the SQL query string
    nbr: the number of '?' in the query
*/
var getItem = function(pathfile) {
    var mItem = {};
    mItem.query = fs.readFileSync(filepath + '/' +pathfile, 'utf-8');
    mItem.nbr = (mItem.query.match(/\?/g) || []).length;
    return mItem;
}

var ct = getItem('from_c_tw.sql');
