/* Simple query string (for now) */

var query="SELECT _id, 'CHINESE' AS LANGTABLE, char AS CHAR " +
    "FROM Cmainland WHERE (char LIKE ? OR mandarin LIKE ? OR mandarin_raw LIKE ?)" +
    " UNION " +
    "SELECT _id, 'TAIWANESE' AS LANGTABLE, char AS CHAR " +
    "FROM Ctw WHERE (char LIKE ? OR mandarin LIKE ? OR mandarin_raw LIKE ? OR bopo LIKE ?) " +
    "UNION " +
    "SELECT _id, 'JAPANESE' AS LANGTABLE, char AS CHAR FROM Cjp WHERE "+
    "(char LIKE ? OR furigana LIKE ? OR romaji LIKE ? OR romaji_raw LIKE ?) " +
    "ORDER BY LANGTABLE";
    
var nbr= (query.match(/\?/g) || []).length;

exports.searchquery={'query':query,'nbr':nbr};